import React, { useState } from 'react';

const Search = () => {
  const [graduationYear, setGraduationYear] = useState('');
  const [firstName, setFirstName] = useState('');
  const [alumniData, setAlumniData] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    try {
      // Replace with the actual API call
      const response = await fetch(`/api/v1/student/search?graduationYear=${grad_year}&name=${fname}`);
      if (response.ok) {
        const data = await response.json();
        setAlumniData(data);
      } else {
        setError('No results found');
      }
    } catch (err) {
      setError('An error occurred while fetching the data');
    }
  };

  return (
    <div>
      <form className="main_form">
        <div className="mb-3" controlid="g_year">
          <label htmlFor="g_year_input" className="form-label">
            Graduation Year
          </label>
          <input
            id="g_year_input"
            className="form-control"
            placeholder="Enter Graduation Year"
            type="number"
            value={graduationYear}
            onChange={(e) => setGraduationYear(e.target.value)}
          />
          <small className="text-muted custom-text form-text">
            Thank you for being a part of IIITB Fam..!
          </small>
        </div>

        <div className="mb-3" controlid="fname">
          <label htmlFor="fname_input" className="form-label">
            First Name
          </label>
          <input
            id="fname_input"
            className="form-control"
            placeholder="Enter First Name"
            type="text"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </div>

        <button
          className="mb-3 btn btn-primary"
          controlid="search_button"
          type="button"
          onClick={handleSearch}
        >
          Search
        </button>
      </form>

      {error && <p className="text-danger">{error}</p>}

      {alumniData && (
        <div>
          <h3>Alumni Data</h3>
          <pre>{JSON.stringify(alumniData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default Search;
